
public class switching {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;
		age = 3;
		
		switch (age ) {
		case 1: System.out.println("crawl"); break;
		case 2: System.out.println("talk"); break;
		case 3: System.out.println("run"); break;
		case 4: System.out.println("swing"); break;
		case 5: System.out.println("friends"); break;
		default: System.out.println("how old?"); break;
		}

	}

}
